import{_ as t,c as a,a3 as o,o as r}from"./chunks/framework.BRvtVtzd.js";const k=JSON.parse('{"title":"Overview of the Secondary Market","description":"","frontmatter":{"meta":[{"name":"title","content":"Overview of the Secondary Market"},{"name":"description","content":"The secondary market for bonds refers to the market where bonds that have already been issued are traded between investors."},{"name":"keywords","content":"Overview of the Secondary Market, Functions"},{"property":"og:title","content":"Overview of the Secondary Market"},{"property":"og:description","content":"The secondary market for bonds refers to the market where bonds that have already been issued are traded between investors."},{"property":"og:type","content":"website"},{"property":"og:image","content":"/images/bond.png"},{"property":"og:url","content":"https://haun84.github.io/bond/secondary-market"}]},"headers":[],"relativePath":"bond/secondary-market.md","filePath":"en/bond/secondary-market.md","lastUpdated":null}'),n={name:"bond/secondary-market.md"};function s(d,e,i,c,l,h){return r(),a("div",null,e[0]||(e[0]=[o('<h1 id="overview-of-the-secondary-market" tabindex="-1">Overview of the Secondary Market <a class="header-anchor" href="#overview-of-the-secondary-market" aria-label="Permalink to &quot;Overview of the Secondary Market&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">Language</p><ul><li><a href="/ko/bond/secondary-market">한국어 페이지: Korean Page</a></li></ul></div><h2 id="overview-of-the-secondary-market-1" tabindex="-1">Overview of the Secondary Market <a class="header-anchor" href="#overview-of-the-secondary-market-1" aria-label="Permalink to &quot;Overview of the Secondary Market&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">Secondary Market</p><ul><li>The <code>secondary market for bonds</code> refers to the market where bonds that have already been issued are traded between investors.</li><li>Therefore, if the <code>issuance market</code> is called the <code>primary market</code>, the <code>secondary market</code> is referred to as the <code>secondary market</code>, which serves as a horizontal transfer mechanism between investors and is also known as a lateral market.</li><li>While the <code>primary market is indirect</code>, the <code>secondary market is continuous, specific, and organized</code>.</li><li>In the past, most investors were long-term holders of bonds until maturity, and thus the need for a highly developed secondary market was not as great.</li><li>However, with the development of the secondary market, investors have been able to sell bonds acquired in the primary market to recover their investment principal or purchase bonds as a means of asset management.</li></ul></div><h2 id="functions-of-the-secondary-market" tabindex="-1">Functions of the Secondary Market <a class="header-anchor" href="#functions-of-the-secondary-market" aria-label="Permalink to &quot;Functions of the Secondary Market&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">Functions of the Secondary Market</p><ul><li>It enhances the <code>marketability</code> and <code>liquidity</code> of <code>bonds</code> issued in the primary market.</li><li>The <code>marketability</code> and <code>liquidity</code> of bonds provided by the <code>secondary market</code> demonstrate the bond&#39;s collateral value.</li><li>It establishes <code>fair pricing for bonds</code> in the secondary market.</li><li>The prices formed in the secondary market serve as a reference for determining the prices of newly issued bonds in the future.</li></ul></div>',6)]))}const y=t(n,[["render",s]]);export{k as __pageData,y as default};
