import{_ as i,c as t,a2 as o,o as n}from"./chunks/framework.wtg6z0M2.js";const u=JSON.parse('{"title":"Safe Blocking Service for Credit Transactions; Preventing Illegal Loans from Voice Phishing and Identity Theft","description":"You can block illegal loans resulting from voice phishing and identity theft in real-time using the Safe Blocking Service for Credit Transactions.","frontmatter":{"title":"Safe Blocking Service for Credit Transactions; Preventing Illegal Loans from Voice Phishing and Identity Theft","description":"You can block illegal loans resulting from voice phishing and identity theft in real-time using the Safe Blocking Service for Credit Transactions.","head":[["meta",{"property":"og:title","content":"Safe Blocking Service for Credit Transactions; Preventing Illegal Loans from Voice Phishing and Identity Theft"}],["meta",{"property":"og:description","content":"You can block illegal loans resulting from voice phishing and identity theft in real-time using the Safe Blocking Service for Credit Transactions."}],["meta",{"property":"og:image","content":"/images/haunblog.png"}],["meta",{"property":"og:url","content":"https://haun84.github.io/feed/money-policy/credit-transaction-safe-block-service"}]]},"headers":[],"relativePath":"feed/money-policy/credit-transaction-safe-block-service.md","filePath":"en/feed/money-policy/credit-transaction-safe-block-service.md","lastUpdated":null}'),a={name:"feed/money-policy/credit-transaction-safe-block-service.md"};function c(r,e,s,l,d,f){return n(),t("div",null,e[0]||(e[0]=[o('<h1 id="safe-blocking-service-for-credit-transactions-preventing-illegal-loans-from-voice-phishing-and-identity-theft" tabindex="-1">Safe Blocking Service for Credit Transactions: Preventing Illegal Loans from Voice Phishing and Identity Theft <a class="header-anchor" href="#safe-blocking-service-for-credit-transactions-preventing-illegal-loans-from-voice-phishing-and-identity-theft" aria-label="Permalink to &quot;Safe Blocking Service for Credit Transactions: Preventing Illegal Loans from Voice Phishing and Identity Theft&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">Language</p><ul><li><a href="/ko/feed/money-policy/credit-transaction-safe-block-service">한국어 페이지: Korean Page</a></li></ul></div><h2 id="safe-blocking-service-for-credit-transactions" tabindex="-1">Safe Blocking Service for Credit Transactions <a class="header-anchor" href="#safe-blocking-service-for-credit-transactions" aria-label="Permalink to &quot;Safe Blocking Service for Credit Transactions&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">Safe Blocking Service for Credit Transactions</p><ul><li>A service to prevent financial damage, such as illegal loans, has been in effect since August.</li><li>The <code>Safe Blocking Service for Credit Transactions</code> allows you to <code>preemptively block</code> new credit transactions such as <code>personal loans</code>, <code>card loans</code>, and `credit card issuance.</li><li><code>Credit transactions</code> refer to transactions where <code>financial institutions lend money to customers</code>, including loans and credit card issuance.</li><li>As soon as you register for the Safe Blocking Service for Credit Transactions, your information is immediately registered with the <code>Korea Credit Information Services</code>, and new credit transactions are blocked in real-time.</li></ul></div><h2 id="how-to-apply-for-the-service" tabindex="-1">How to Apply for the Service <a class="header-anchor" href="#how-to-apply-for-the-service" aria-label="Permalink to &quot;How to Apply for the Service&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">How to Apply for the Service</p><ul><li>You must <code>visit</code> your <code>current bank</code>, <code>NongHyup</code>, <code>Suhyup</code>, or a <code>post office branch</code> in person. The process is quick and easy as it is completed with just <code>identity verification</code>. You don’t need any additional documents—just bring your <code>ID</code>.</li><li>Most financial institutions started offering the service on <code>August 23</code>. Some internet-only banks, insurance companies, and specialized credit financial institutions that are still developing their systems will offer the service from September.</li></ul></div><h2 id="how-to-conduct-credit-transactions-after-applying-for-the-service" tabindex="-1">How to Conduct Credit Transactions After Applying for the Service <a class="header-anchor" href="#how-to-conduct-credit-transactions-after-applying-for-the-service" aria-label="Permalink to &quot;How to Conduct Credit Transactions After Applying for the Service&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">How to Conduct Credit Transactions After Applying for the Service</p><ul><li>If you want to conduct <code>credit transactions</code> such as taking out a loan after applying for the service, you’ll need to <code>cancel the service</code> by visiting the financial institution where you applied for it, such as a bank, NongHyup, Suhyup, or post office.</li><li>At that time, the staff will carefully check whether the service is being canceled due to <code>voice phishing</code> or <code>identity theft</code>.</li><li>Since <code>financial fraud</code> affects everyone, from teenagers to the elderly, thorough precaution is essential.</li></ul></div>',8)]))}const g=i(a,[["render",c]]);export{u as __pageData,g as default};
