import{_ as o}from"./chunks/bond-primary-market.CJ4he09I.js";import{_ as a,c,a2 as t,o as d}from"./chunks/framework.wtg6z0M2.js";const k=JSON.parse('{"title":"발행시장의 구조","description":"자본의 수요자인 발행주체(기업, 정부, 지방자치단체, 금융기관)에 의하여 신규로 발행되는 채권이 일반투자자 또는 기관투자자에게 매각됨으로써 자본이 투자자로부터 발행주체에게 이전되는 일체의 과정을 포함하는 시장을 발행시장 이라고 하며, 이를 1차적 시장(Primary Market)이라고도 한다.","frontmatter":{"title":"발행시장의 구조","description":"자본의 수요자인 발행주체(기업, 정부, 지방자치단체, 금융기관)에 의하여 신규로 발행되는 채권이 일반투자자 또는 기관투자자에게 매각됨으로써 자본이 투자자로부터 발행주체에게 이전되는 일체의 과정을 포함하는 시장을 발행시장 이라고 하며, 이를 1차적 시장(Primary Market)이라고도 한다.","head":[["meta",{"property":"og:title","content":"발행시장의 구조"}],["meta",{"property":"og:description","content":"자본의 수요자인 발행주체(기업, 정부, 지방자치단체, 금융기관)에 의하여 신규로 발행되는 채권이 일반투자자 또는 기관투자자에게 매각됨으로써 자본이 투자자로부터 발행주체에게 이전되는 일체의 과정을 포함하는 시장을 발행시장 이라고 하며, 이를 1차적 시장(Primary Market)이라고도 한다."}],["meta",{"property":"og:image","content":"/images/bond.png"}],["meta",{"property":"og:url","content":"https://haun84.github.io/ko/bond/primary-market-structure"}]]},"headers":[],"relativePath":"ko/bond/primary-market-structure.md","filePath":"ko/bond/primary-market-structure.md","lastUpdated":null}'),r={name:"ko/bond/primary-market-structure.md"};function i(l,e,s,n,u,m){return d(),c("div",null,e[0]||(e[0]=[t('<h1 id="발행시장의-구조" tabindex="-1">발행시장의 구조 <a class="header-anchor" href="#발행시장의-구조" aria-label="Permalink to &quot;발행시장의 구조&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">Language</p><ul><li><a href="/bond/primary-market-structure">English Page: 영어 페이지</a></li></ul></div><h2 id="발행시장의-구조-1" tabindex="-1">발행시장의 구조 <a class="header-anchor" href="#발행시장의-구조-1" aria-label="Permalink to &quot;발행시장의 구조&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">채권 발행시장</p><ul><li><code>자본의 수요자</code>인 <code>발행주체</code> (기업, 정부, 지방자치단체, 금융기관)에 의하여 <code>신규로 발행되는 채권</code>이 <code>일반투자자 또는 기관투자자</code>에게 매각됨으로써 <code>자본</code>이 <code>투자자로부터 발행주체에게 이전</code>되는 일체의 과정을 포함하는 시장을 발행시장 이라고 하며, 이를 1차적 시장(Primary Market)이라고도 한다.</li><li><code>발행시장</code>은 <code>발행자</code>, <code>발행기관</code>, <code>투자자</code>로 구성되어 있으며 발행주체가 투자자로부터 직접자금을 조달하는 <code>직접발행방법</code>과 발행기관으로 하여금 발행업무와 발행채권을 인수케하여 투자자에게 매출하는 <code>간접발행방법</code>이 있다.</li></ul></div><p><img src="'+o+'" alt="Bond-Primary-Market"></p><h2 id="발행자" tabindex="-1">발행자 <a class="header-anchor" href="#발행자" aria-label="Permalink to &quot;발행자&quot;">​</a></h2><p>채권발행에 의해 <code>자금을 조달하는 주체</code>로서 <code>정부</code>, <code>공공단체</code>, <code>특별법에 의해 설립된 법인</code>, <code>상법상의 주식회사</code> 등이 있다.</p><h2 id="투자자" tabindex="-1">투자자 <a class="header-anchor" href="#투자자" aria-label="Permalink to &quot;투자자&quot;">​</a></h2><p>채권 발행시장에서 모집, 매출되는 채권의 청약에 응하여 <code>발행자가 발행한 채권을 취득하는 자</code>로서 채권투자의 형태로 <code>자금을 공급하는 주체</code>를 총칭한다.</p><h2 id="발행기관" tabindex="-1">발행기관 <a class="header-anchor" href="#발행기관" aria-label="Permalink to &quot;발행기관&quot;">​</a></h2><p>채권발행에 있어 발행자와 투자자 사이에서 채권발행에 필요한 <code>제반업무를 담당</code>하고 발행에 따른 <code>위험부담</code> 및 <code>판매기능</code>까지 발행주체를 대신하여 행하는 기관이다.</p><p>이러한 책임과 위험을 분산하고 발행증권의 매출을 원활히 하기 위해 여러 발행기관이 공동으로 하나의 유가증권 발행에 참여하는 것이 보통이며, 발행기관은 그 역할에 따라 <code>간사회사</code>, <code>인수기관</code>, <code>청약기관</code>으로 나눌 수 있다.</p><h3 id="간사회사" tabindex="-1">간사회사 <a class="header-anchor" href="#간사회사" aria-label="Permalink to &quot;간사회사&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">간사회사</p><ul><li><code>간사회사</code>는 채권발행에 대한 사무처리, 발행과 관련된 자문 등 <code>채권발행업무를 총괄</code>하며 인수단을 구성하는 역할을 한다.</li><li>발행규모가 클 경우 간사단을 구성하여 공동으로 간사업무를 수행하는데, 이 중 가장 핵심적인 역할을 담당하는 회사를 <code>주간사회사</code>라 하며 기타 간사회사를 <code>공동간사회사</code>라고 한다.</li></ul></div><h3 id="인수기관" tabindex="-1">인수기관 <a class="header-anchor" href="#인수기관" aria-label="Permalink to &quot;인수기관&quot;">​</a></h3><div class="danger custom-block"><p class="custom-block-title">인수기관</p><ul><li><code>인수기관</code>은 간사회사와 협의하여 발행채권을 직접 매입하는 <code>인수기능</code>을 행함으로써 채권발행자로 하여금 거액의 자금조달을 가능하게 하는 기관들이다.</li><li>이들은 인수채권을 자신들이 보유하기도 하지만 일반투자자 및 청약기관에 매도하는 역할을 하기도 한다.</li></ul></div><h3 id="청약기관" tabindex="-1">청약기관 <a class="header-anchor" href="#청약기관" aria-label="Permalink to &quot;청약기관&quot;">​</a></h3><div class="info custom-block"><p class="custom-block-title">청약기관</p><ul><li><code>청약기관</code>은 인수에 따르는 위험을 부담하는 인수기관과는 달리 신규발행채권을 매입하고자 하는 <code>불특정 다수의 투자자들에 대한 청약업무만을 대행</code>한다.</li><li>청약업무는 일반적으로 인수업무를 허가받은 증권사들의 본지점망을 통하여 이루어진다.</li></ul></div>',18)]))}const b=a(r,[["render",i]]);export{k as __pageData,b as default};
