import{_ as l,B as o,c as e,a2 as a,G as n,o as s}from"./chunks/framework.rTf5PlzF.js";const k=JSON.parse('{"title":"산업연관표 - 투입산출표의 응용","description":"산업연관표 편제와 분석 기법이 발전함에 따라 기본적인 형태의 투입산출표를 응용하여 불변가격 투입산출표나 지역산업연관표, 세계산업연관표 등도 작성하고 있다.","frontmatter":{"title":"산업연관표 - 투입산출표의 응용","description":"산업연관표 편제와 분석 기법이 발전함에 따라 기본적인 형태의 투입산출표를 응용하여 불변가격 투입산출표나 지역산업연관표, 세계산업연관표 등도 작성하고 있다.","head":[["meta",{"property":"og:title","content":"산업연관표 - 투입산출표의 응용"}],["meta",{"property":"og:description","content":"산업연관표 편제와 분석 기법이 발전함에 따라 기본적인 형태의 투입산출표를 응용하여 불변가격 투입산출표나 지역산업연관표, 세계산업연관표 등도 작성하고 있다."}],["meta",{"property":"og:image","content":"/images/haunblog.png"}],["meta",{"property":"og:url","content":"https://haun84.github.io/ko/economic-indicators/input-output-analysis/application"}]]},"headers":[],"relativePath":"ko/economic-indicators/input-output-analysis/application.md","filePath":"ko/economic-indicators/input-output-analysis/application.md","lastUpdated":null}'),c={name:"ko/economic-indicators/input-output-analysis/application.md"};function r(p,t,u,d,m,h){const i=o("AdsInarticle");return s(),e("div",null,[t[0]||(t[0]=a('<h1 id="투입산출표의-응용" tabindex="-1">투입산출표의 응용 <a class="header-anchor" href="#투입산출표의-응용" aria-label="Permalink to &quot;투입산출표의 응용&quot;">​</a></h1><h2 id="투입산출표의-응용-1" tabindex="-1">투입산출표의 응용 <a class="header-anchor" href="#투입산출표의-응용-1" aria-label="Permalink to &quot;투입산출표의 응용&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">투입산출표의 응용</p><ul><li>산업연관표 편제와 분석 기법이 발전함에 따라 기본적인 형태의 투입산출표를 응용하여 불변가격 투입산출표나 지역산업연관표, 세계산업연관표 등도 작성하고 있다.</li></ul></div><h2 id="불변가격-투입산출표" tabindex="-1">불변가격 투입산출표 <a class="header-anchor" href="#불변가격-투입산출표" aria-label="Permalink to &quot;불변가격 투입산출표&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">불변가격 투입산출표</p><ul><li>서로 다른 연도 간의 생산기술구조 변화를 분석하는 데는 물량 단위의 개념인 불변가격 투입산출표가 필요하다.</li><li>해당 연도의 재화 및 서비스의 가격, 즉 경상가격으로 작성된 투입산출표에는 물량 요인과 가격 요인이 함께 포함되어 있어 생산기술구조 변화를 정확하게 찾아내는 데 한계가 있기 때문이다.</li><li>불변가격 투입산출표는 경상가격으로 작성된 투입산출표를 기준연도의 가격을 기준으로 조정하여 재작성한 것이다.</li><li>불변가격 투입산출표는 오일쇼크, 환율 급상승 등의 영향으로 상대가격이 급격히 변동되는 경우 순수한 기술구조의 변화를 파악하는 데 유용하며 경제예측이나 계획수립 시에 필요한 투입계수의 추정 등에도 널리 활용될 수 있다. 한국은행에서는 1989년에 처음으로 1985년 가격 기준으로 환가한 1975-1980-1985년 연결 불변가격 투입산출표를 작성·발표하였다. 이후 1994년부터 2009년까지 5년 간격으로 3개년을 연결한 불변가격 투입산출표를 작성·발표하였다.</li></ul></div>',5)),n(i),t[1]||(t[1]=a('<h2 id="지역산업연관표" tabindex="-1">지역산업연관표 <a class="header-anchor" href="#지역산업연관표" aria-label="Permalink to &quot;지역산업연관표&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">지역산업연관표</p><ul><li>산업연관표는 일반적으로 한 나라의 경제를 대상으로 작성되므로 각 산업의 생산기술구조가 동일하다고 가정한다. 그러나 같은 산업이라도 지역에 따라 생산기술구조가 다를 수 있다. 예를 들어 전력 생산의 경우 어떤 지역에서는 수력에 크게 의존하고 다른 지역에서는 화력이나 원자력을 많이 사용할 수 있다. 또한 일반적으로 도시지역에서는 농촌지역 등으로 공산품 이출이 많고 농촌지역으로부터의 농산물 이입이 많은 반면 농촌지역에서는 이와 반대 현상이 나타나는 등 각 지역에 따라 상품의 거래형태가 다를 수 있다.</li><li>지역산업연관표는 지역별로 상이한 생산기술구조와 거래형태를 반영하여 지역 및 산업 간 상호의존관계를 분석할 수 있도록 한 투입산출표이다. 지역산업연관표에는 한 지역 내의 경제구조만을 대상으로 하는 지역 내 산업연관표와 여러 지역 간의 연관관계를 나타내는 지역 간 산업연관표가 있다.</li><li>먼저 지역 내 산업연관표는 한 지역의 생산기술구조를 나타내는 투입산출표로서 앞에서 살펴본 국가경제를 대상으로 하는 투입산출표와 같은 형식을 취하게 된다. 즉, 해당 지역에서 다른 지역으로 상품을 판매하는 이출과 다른 지역으로부터 상품을 구입하는 이입을 각각 수출입과 같은 방식으로 계상한다. 이처럼 지역 내 산업연관표에서는 해당 지역 내의 산업 간 거래관계가 상세히 표시되지만 다른 지역과의 거래관계를 이출 또는 이입으로 일괄 계상하므로 어느 지역의 어떤 산업과의 거래관계인지 파악할 수 없다.</li><li>이와 달리 지역 간 산업연관표는 생산활동에 대한 지역 간의 상호의존관계를 분석하기 위해서 지역 간의 이출입을 매개로 하여 지역 및 산업 간의 거래관계를 구분 기록한 투입산출표이다.</li><li>지역 간 산업연관표의 가로 방향은 한 나라 안에서 각 지역에서 산출된 상품이 어느 지역의 어떤 산업으로 얼마만큼 배분되는지를 나타낸다. 그리고 세로 방향은 각 지역에서 생산활동을 영위하기 위하여 어느 지역의 어떤 산업의 생산물이 얼마만큼 투입되는지를 나타낸다. 따라서 지역 간 산업연관표는 특정 지역의 상품에 대한 최종수요가 각 지역의 각 산업에 미치는 생산, 고용 등 제반 경제적 파급효과를 측정할 수 있게 되므로 지역경제분석이나 균형 있는 지역경제개발계획 수립 등에 널리 활용될 수 있다. 한국은행에서는 2007년에 국내 최초로 2003년 기준 6개 권역의 지역 투입산출표를 작성·발표하였으며 2009년에 2005년기준, 2015년에 2013년 기준. 2020년에 2015년 기준의 17개 광역시·도 지역산업연관표를 작성·발표하였다.</li><li>지역산업연관표의 구조를 알아보기 위해 2015년 지역산업연관표를 &lt;표 2-15&gt;와 같이 7개 권역으로 정리하였다. &lt;표 2-15&gt;를 보면 2015년 중 수도권은 1,793조 원을 생산하기 위하여 수도권, 충청권, 전라권 등으로부터 중간재를 각각 570조 원, 70조 원, 38조 원 등 총 786조원을 구입하고 외국에서 176조 원을 수입하였음을 알 수 있다. 수도권의 생산물은 수도권, 충청권, 전라권 등 각 지역의 생산활동을 위한 중간재로 각각 570조 원, 69조 원, 38조 원 등 총 790조 원이 투입되었음을 알 수 있다. 그리고 이들 지역의 최종수요로 각각 603조 원, 32조 원, 22조 원 등 총 721조 원이 판매되고 외국에는 282조 원이 수출되었음이 나타나 있다</li></ul></div><h2 id="세계산업연관표" tabindex="-1">세계산업연관표 <a class="header-anchor" href="#세계산업연관표" aria-label="Permalink to &quot;세계산업연관표&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">세계산업연관표</p><ul><li>한편 지역 간 산업연관표를 국가와 국가 사이의 관계에 적용하여 작성한 것이 세계산업연관표이다.</li><li>이 표는 국가 간의 수출입을 매개로 하여 작성되는데, 한 국가의 수출은 다른 국가의 수입이며 한 국가의 수입은 다른 국가의 수출이고, 수입된 물품은 그 용도에 따라 각각 중간재 또는 최종재로 사용된다. 따라서 각 국가의 수입을 국가별 용도별로 구분하고 수출 또한 국가별로 나눈 다음 이를 매개로 각 국가의 투입산출표를 상호 연결하면 여러 국가의 투입산출표를 하나의 투입산출표로 만들 수 있다. 이렇게 만들어진 세계산업연관표(ICIO: Inter-Country Input-Output table)는 각 국가의 상이한 생산기술구조와 교역형태를 반영하게 된다. 이처럼 세계산업연관표를 이용하면 우리나라의 최종수요가 변화할 때의 각국에 대한 수입유발효과와 각국의 최종수요변화에 따른 우리나라의 생산유발효과의 측정이 가능하여 국가 및 산업 간 상호연관관계를 분석할 수 있다.</li></ul></div>',4))])}const f=l(c,[["render",r]]);export{k as __pageData,f as default};
