import{_ as e,c as o,a3 as l,o as a}from"./chunks/framework.BRvtVtzd.js";const c="/images/produce-asset-capital-stock.png",d="/images/non-produce-asset-capital-stock.png",b=JSON.parse('{"title":"국민대차대조표 작성 방법","description":"","frontmatter":{"meta":[{"name":"title","content":"국민대차대조표 작성 방법"},{"name":"description","content":"국민계정체계(SNA)는 국민대차대조표에 기록되는 모든 자산이 다른 국민계정들과 일관성을 유지하도록 권고하고 있으며 우리나라의 국민대차대조표도 다른 국민계정통계들과 상호 일관성을 유지하며 연계되어 있다."},{"name":"keywords","content":"국민대차대조표 작성 방법"},{"property":"og:title","content":"국민대차대조표 작성 방법"},{"property":"og:description","content":"국민계정체계(SNA)는 국민대차대조표에 기록되는 모든 자산이 다른 국민계정들과 일관성을 유지하도록 권고하고 있으며 우리나라의 국민대차대조표도 다른 국민계정통계들과 상호 일관성을 유지하며 연계되어 있다."},{"property":"og:type","content":"website"},{"property":"og:image","content":"/images/haunblog.png"},{"property":"og:url","content":"https://haun84.github.io/ko/economic-indicators/national-balance-sheet/hot-to-make"}]},"headers":[],"relativePath":"ko/economic-indicators/national-balance-sheet/how-to-make.md","filePath":"ko/economic-indicators/national-balance-sheet/how-to-make.md","lastUpdated":null}'),i={name:"ko/economic-indicators/national-balance-sheet/how-to-make.md"};function s(n,t,r,h,p,u){return a(),o("div",null,t[0]||(t[0]=[l('<h1 id="국민대차대조표-작성-방법" tabindex="-1">국민대차대조표 작성 방법 <a class="header-anchor" href="#국민대차대조표-작성-방법" aria-label="Permalink to &quot;국민대차대조표 작성 방법&quot;">​</a></h1><h2 id="국민대차대조표-작성-방법-1" tabindex="-1">국민대차대조표 작성 방법 <a class="header-anchor" href="#국민대차대조표-작성-방법-1" aria-label="Permalink to &quot;국민대차대조표 작성 방법&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">국민대차대조표 작성 방법</p><ul><li><code>국민계정체계(SNA)</code>는 국민대차대조표에 기록되는 모든 자산이 다른 국민계정들과 일관성을 유지하도록 권고하고 있으며 우리나라의 국민대차대조표도 다른 <code>국민계정통계</code>들과 <code>상호 일관성</code>을 유지하며 연계되어 있다.</li><li>예를 들어 <code>고정자산의 자본스톡</code>은 <code>국민소득통계의 투자를 기초</code>로 추계하고 <code>국민대차대조표의 자본스톡으로부터 산출되는 고정자본소모</code>는 다시 국민소득통계 작성에 사용되고 있다. 또한 국민대차대조표에 기록되는 <code>금융 자산</code> 및 <code>부채</code>는 <code>자금순환계정</code>의 <code>금융자산부채잔액표(스톡통계)</code>와 <code>금융거래표(플로통계)</code>를 이용하여 작성하고 있다.</li><li>한편 국민대차대조표에 기록되는 모든 항목은 원칙적으로 <code>대차대조표 작성 시점</code>의 <code>시장가격</code>으로 평가해야 한다.</li><li>하지만 모든 자산에 대한 정확한 가격자료를 확보하기는 현실적으로 어렵다. 따라서 실제 시장에서 거래된 가격자료를 통해 자산가액을 추계하는 것을 원칙으로 하되, 가격에 대한 정보가 없을 경우에는 해당 자산을 생산하는 데 소요된 비용으로 계산(<code>비용접근법</code>)하거나, 미래수익의 현재가치로 환산(<code>소득접근법</code>)하거나, 유사 거래사례를 비교하여(<code>거래사례비교법</code>) 추정하고 있다.</li><li>시장가격으로 자산가액을 평가하는 방법에는 <code>물량가격법</code>, <code>영구재고법</code>, <code>순현재가치법</code> 등이 있으며 자산별 특성을 고려하여 평가가 이루어진다.</li></ul></div><h2 id="생산자산의-작성" tabindex="-1">생산자산의 작성 <a class="header-anchor" href="#생산자산의-작성" aria-label="Permalink to &quot;생산자산의 작성&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">생산자산의 작성</p><ul><li>국민대차대조표에 기록되는 생산자산 스톡은 과거 투자 시계열을 누적하되 자산의 감가상각 등을 반영하는 <code>영구재고법(PIM: Perpetual Inventory Method)</code>으로 추계되고 있으며, 자산의 특성에 따라 다른 방법도 부분적으로 사용되고 있다. <img src="'+c+'" alt="produce-asset-capital-stock"></li></ul></div><h2 id="생산자산-고정자산" tabindex="-1">생산자산: 고정자산 <a class="header-anchor" href="#생산자산-고정자산" aria-label="Permalink to &quot;생산자산: 고정자산&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">고정자산</p><ul><li><code>건설자산</code>, <code>운송장비</code>, <code>기계류</code>, <code>지식재산생산물</code> 등 대부분의 <code>고정자산</code>에 대해서는 <code>영구재고법</code>을 이용하여 자본스톡을 추계하고 있다.</li><li><code>영구재고법</code>은 국민소득통계의 투자(총고정자본형성)를 누적하되 내용연수가 지난 자본재의 폐기, 생산과정에서 발생하는 자본재의 마모로 인한 생산효율 저하, 감가상각 등을 차감하여 추계하는 방식이다.</li><li>일반적으로 영구재고법은 어떠한 감가상각 함수를 적용하는가에 따라 <code>정액법</code>, <code>정률법</code> 및 <code>효율현가법</code>으로 나뉘는데 우리나라는 <code>총자본스톡(total capital stock)</code>, <code>생산자본스톡(productive capital stock)</code>, <code>순자본스톡(net capital stock)</code>을 체계적으로 계산할 수 있는 <code>효율현가법(present value of efficiency model)</code>을 사용하고 있다.</li><li><code>효율현가법</code>에 의해 도출된 총자본스톡은 폐기된 후 남은 자본재의 가액을 단순히 더한 것으로 연령 증가에 따른 가치 하락을 감안하지 않고 신제품으로 구입할 때의 가격으로 평가한 것이다.</li><li>이에 반해 <code>생산 자본스톡</code>은 시간의 경과에 따른 자본재의 생산능력(효율) 하락을 반영하고 있어 실제 생산에 투입된 자본물량을 측정하는 데 적합하다.</li><li>마지막으로 <code>순자본스톡</code>은 국민대차대조표에 기록되는 스톡으로 연령증가에 따른 가격하락까지 반영하고 있어 <code>부(富)의 스톡(wealth capital stock)</code>이라고도 한다.</li><li>한편 육성생물자원은 수량(물량)에 가격을 직접 곱하는 물량가격법을 이용해 순자본스톡을 추계하고 있다. 이는 사육두수, 재배면적 등 육성생물자원의 수량에 관한 행정자료가 대체로 잘 정비되어 있기 때문이다.</li></ul></div><h2 id="생산자산-재고자산" tabindex="-1">생산자산: 재고자산 <a class="header-anchor" href="#생산자산-재고자산" aria-label="Permalink to &quot;생산자산: 재고자산&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">재고자산</p><ul><li>우리나라의 <code>재고자산</code>은 <code>산업별 기초자료를 이용하여 직접 추계하는 방식</code>을 이용하고 있다. 이는 산업별로 재고와 관련된 기초자료가 잘 갖추어져 있고 생산성 분석과 같은 거시경제분석도 주로 산업별로 이루어진다는 점을 고려한 것이다.</li><li>구체적으로 농림어업 및 석유재고의 경우 수량 정보를 이용하여 물량가격법으로 추계하며 광공업, 서비스업 등은 행정통계, 과세자료, 기업회계자료 등 다양한 기초자료를 종합적으로 사용하여 추계한다.</li></ul></div><h2 id="비생산자산의-작성" tabindex="-1">비생산자산의 작성 <a class="header-anchor" href="#비생산자산의-작성" aria-label="Permalink to &quot;비생산자산의 작성&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">비생산자산의 작성</p><ul><li>국민대차대조표에는 <code>토지자산</code>, <code>지하자원</code> 등과 같은 <code>비생산자산</code>도 기록된다. 비생산자산을 추정하지 않을 경우 국가경제 혹은 개별 경제주체가 보유하고 있는 부의 규모가 과소평가될 수 있기 때문이다.</li><li>그러나 비생산자산은 시장 거래가 활발하지 않거나 채굴되지 않은 자연 그대로의 상태인 만큼 가치를 평가하는 데 어려움이 있다. <img src="'+d+'" alt="non-produce-asset-capital-stock"></li></ul></div><h2 id="비생산자산-토지자산" tabindex="-1">비생산자산: 토지자산 <a class="header-anchor" href="#비생산자산-토지자산" aria-label="Permalink to &quot;비생산자산: 토지자산&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">토지자산</p><ul><li><code>토지자산</code>의 가치는 토지면적에 평균 토지가격을 곱하는 <code>물량가격법</code>을 사용하여 추정하고 있다.</li><li>구체적으로는 공시가격, 실거래가격, 감정평가가격을 이용하여 산출한 시장가격에 토지면적을 곱한 결과를 국민계정체계에 부합되도록 재분류하여 기록하고 있다.</li></ul></div><table tabindex="0"><thead><tr><th style="text-align:left;">토지 분류</th><th style="text-align:left;">분류 설명</th></tr></thead><tbody><tr><td style="text-align:left;">주거용 건물 부속토지</td><td style="text-align:left;">주택용 토지</td></tr><tr><td style="text-align:left;">비주거용 건물 부속토지</td><td style="text-align:left;">상가, 공장, 학교, 주차장 등의 토지</td></tr><tr><td style="text-align:left;">구축물 부속토지</td><td style="text-align:left;">도로, 철도, 제방 등의 토지</td></tr><tr><td style="text-align:left;">농경지</td><td style="text-align:left;">전, 답, 과수원, 목장, 염전</td></tr><tr><td style="text-align:left;">임야</td><td style="text-align:left;">산</td></tr><tr><td style="text-align:left;">문화오락용 토지</td><td style="text-align:left;">공원, 체육용지, 유원지 등의 토지</td></tr><tr><td style="text-align:left;">기타 토지</td><td style="text-align:left;">기타</td></tr></tbody></table><h2 id="비생산자산-지하자원-및-입목자산" tabindex="-1">비생산자산: 지하자원 및 입목자산 <a class="header-anchor" href="#비생산자산-지하자원-및-입목자산" aria-label="Permalink to &quot;비생산자산: 지하자원 및 입목자산&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">지하자원 및 입목자산</p><ul><li><code>지하자원</code>으로 기록되는 대상 자원은 <code>금속광물 5종</code>, <code>비금속광물 7종</code>, <code>천연가스</code> 등이다.</li><li>지하자원의 가치 측정방법은 UN, OECD 등 국제기구의 권고에 따라 미래의 기대수익을 현재가치로 환산하는 순현재가치법을 사용하고 있다.</li><li>기대수익은 채굴되지 않는 자연 상태로 평가하기 위해 시장가격에서 채굴비용을 뺀 값에 생산량을 곱해 추정한다.</li><li>입목자산으로는 활엽수, 침엽수, 혼효림이 있으며 지하자원과 마찬가지로 순현재가치법을 사용하여 계산하고 있다. 다만, 자료의 한계나 측정의 용이성 등을 위해 순현재가치법을 일부 변형하여 물량가격법과 비슷한 식으로 계산한다.</li><li>여기에서 입목상태 환산비율은 벌채되지 않고 산에 서 있는 상태에서의 나무가격을 추정하기 위한 것이다. 즉, 벌채된 후 시장에서 거래되는 나무가격에 비해 벌채비용, 운반비용과 유통마진을 제외하였을 때의 가격이 몇% 수준인지를 나타내는 것이다. 또한 입목축적량이란 입목자산의 총량(부피)을 의미한다.</li></ul><div class="language-md vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">md</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  입목자산 = 시장가격 X 입목상태 환산비율 X 입목축적량</span></span></code></pre></div></div><table tabindex="0"><thead><tr><th style="text-align:left;">지하자원 분류</th><th style="text-align:left;">해당 광물</th></tr></thead><tbody><tr><td style="text-align:left;">금속광물, 천연가스</td><td style="text-align:left;">금, 은, 연, 아연, 철, 천연가스, 원유</td></tr><tr><td style="text-align:left;">비금속광물</td><td style="text-align:left;">석회석, 납석, 규석, 장석, 고령토, 운모, 활석</td></tr></tbody></table>',17)]))}const g=e(i,[["render",s]]);export{b as __pageData,g as default};
