import{_ as o,c,a3 as d,o as a}from"./chunks/framework.BRvtVtzd.js";const l="/images/economic-indicators/non-financial-asset.png",t="/images/economic-indicators/national-balance-sheet-form.png",b=JSON.parse('{"title":"국민대차대조표 (NBS; National Balance Sheets)","description":"국민대차대조표 (NBS; National Balance Sheets)는 연말을 기준으로 가계, 기업, 정부 등 개별 경제주체들 및 국가 전체가 보유하고 있는 부의 규모를 보여주는 통계이다.","frontmatter":{"title":"국민대차대조표 (NBS; National Balance Sheets)","description":"국민대차대조표 (NBS; National Balance Sheets)는 연말을 기준으로 가계, 기업, 정부 등 개별 경제주체들 및 국가 전체가 보유하고 있는 부의 규모를 보여주는 통계이다.","head":[["meta",{"property":"og:title","content":"국민대차대조표 (NBS; National Balance Sheets)"}],["meta",{"property":"og:description","content":"국민대차대조표 (NBS; National Balance Sheets)는 연말을 기준으로 가계, 기업, 정부 등 개별 경제주체들 및 국가 전체가 보유하고 있는 부의 규모를 보여주는 통계이다."}],["meta",{"property":"og:image","content":"/images/haunblog.png"}],["meta",{"property":"og:url","content":"https://haun84.github.io/ko/economic-indicators/national-balance-sheet/national-balance-sheet"}]]},"headers":[],"relativePath":"ko/economic-indicators/national-balance-sheet/national-balance-sheet.md","filePath":"ko/economic-indicators/national-balance-sheet/national-balance-sheet.md","lastUpdated":null}'),i={name:"ko/economic-indicators/national-balance-sheet/national-balance-sheet.md"};function n(s,e,r,h,m,p){return a(),c("div",null,e[0]||(e[0]=[d('<h1 id="국민대차대조표" tabindex="-1">국민대차대조표 <a class="header-anchor" href="#국민대차대조표" aria-label="Permalink to &quot;국민대차대조표&quot;">​</a></h1><h2 id="국민대차대조표-nbs-national-balance-sheets" tabindex="-1">국민대차대조표 (NBS; National Balance Sheets) <a class="header-anchor" href="#국민대차대조표-nbs-national-balance-sheets" aria-label="Permalink to &quot;국민대차대조표 (NBS; National Balance Sheets)&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">국민대차대조표 (NBS; National Balance Sheets)</p><ul><li><code>국민대차대조표(NBS: National Balance Sheets)</code>는 <code>연말을 기준</code>으로 <code>가계</code>, <code>기업</code>, <code>정부</code> 등 <code>개별 경제주체들</code> 및 <code>국가 전체</code>가 보유하고 있는 <code>부의 규모</code>를 보여주는 통계로 <code>기업의 재무상태표</code>와 여러 면에서 비슷하다.</li><li><code>기업</code>은 <code>회계연도 말 기준</code>으로 <code>연중 경제활동의 결과</code> 보유하게 된 <code>자산</code>과 <code>부채</code>를 <code>기업회계 기준</code>에 따라 <code>기록</code>한다. <code>국민대차대조표</code>도 이와 비슷하게 <code>특정 시점(보통 연말)을 기준</code>으로 <code>국제 기준</code>인 <code>국민계정체계(SNA; System of National Accounts)</code>에 따라 <code>국가경제가 보유</code>한 <code>자산</code>과 <code>부채</code>를 모두 기록하고 있다.</li><li>국민대차대조표는 SNA 이행의 최상위 단계로 규정될 정도로 편제가 어려운 통계이다. 세계적으로 <code>토지자산을 포함하여 완전한 형태의 국민대차대조표</code>를 작성하고 있는 국가는 호주, 캐나다, 프랑스, 일본, 체코 등 일부 국가에 불과하다.</li><li>우리나라는 오랫동안 연구와 개발에 노력해온 <code>한국은행</code>과 국부통계를 작성해 오던 <code>통계청</code>이 협력하여 2014년 5월 국민대차대조표를 최초로 발표하였다. 이에 따라 우리나라는 <code>국민계정체계</code>의 <code>5대 통계</code>를 모두 편제하는 나라에 속하게 되었다.</li></ul></div><h2 id="국민대차대조표의-개념-및-작성의의" tabindex="-1">국민대차대조표의 개념 및 작성의의 <a class="header-anchor" href="#국민대차대조표의-개념-및-작성의의" aria-label="Permalink to &quot;국민대차대조표의 개념 및 작성의의&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">국민대차대조표의 개념 및 작성의의</p><ul><li><code>국민대차대조표</code>는 <code>특정 시점</code>에서 국민경제 또는 각 경제주체가 보유하고 있는 <code>유·무형 실물자산</code>, <code>금융자산</code> 및 <code>금융부채</code>의 <code>규모</code>와 <code>그 증감</code>을 기록한 <code>스톡통계</code>이다.</li><li>즉, 국민대차대조표는 <code>일정 기간 동안 경제활동을 통해 창출된 소득</code>이 어떻게 부로 축적되는지를 정리한 표라고 할 수 있다. 특히 국민경제 전체의 순자산은 흔히 <code>국부(國富, national wealth)</code>라 불리며 <code>국내경제가 보유한 비금융자산</code>과 <code>순대외금융자산의 합계</code>와 동일하다.</li><li>국민대차대조표의 작성으로 우리나라는 SNA 이행의 최상위 단계를 달성함으로써 완전한 국민계정체계를 구현하게 되었다.</li><li>또한 <code>자본스톡</code>을 통해 측정되는 <code>고정자본소모</code>는 국민계정 내 스톡-플로 항등식 관계를 성립시킴으로써 <code>스톡(stock)통계</code>와 <code>플로(flow)통계</code> 간 정합성을 높일 수 있는 토대를 마련하였다.</li><li>아울러 국민대차대조표는 국민경제 전체 부의 수준을 파악할 수 있게 해 줄 뿐만 아니라 다양한 거시경제 분석에도 이용될 수 있다.</li></ul></div><h2 id="국민대차대조표의-구성" tabindex="-1">국민대차대조표의 구성 <a class="header-anchor" href="#국민대차대조표의-구성" aria-label="Permalink to &quot;국민대차대조표의 구성&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">국민대차대조표의 구성</p><ul><li>국민대차대조표는 <code>비금융자산</code>과 <code>금융자산</code> 및 <code>금융부채</code>로 구분된다.</li><li>먼저 <code>비금융자산</code>은 <code>금융 자산이 아닌 모든 자산</code>을 말하며 생산활동의 결과물인지 아닌지에 따라 <code>생산자산(produced assets)</code>과 <code>비생산자산(non-produced assets)</code>으로 구분된다.</li><li><code>생산자산</code>은 생산과정을 거쳐 만들어진 <code>건물</code>, <code>도로</code>, <code>자동차</code>, <code>기계류</code>, <code>연구개발</code> 등과 같은 <code>유·무형 비금융자산</code>을 말한다.</li><li>반면 <code>비생산자산</code>은 생산과정을 거치지 않고 자연 상태로 존재하는 <code>토지</code>, <code>지하자원</code>, <code>입목자산</code> 등을 말한다.</li></ul><p><img src="'+l+'" alt="non-financial-asset"></p><ul><li><code>금융자산</code> 및 <code>금융부채</code>는 각 경제주체가 경제활동을 하는 과정에서 취득하거나 발생한 모든 금융자산 및 금융부채를 말한다.</li><li><code>중앙은행이 보유하고 있는 금</code>과 <code>IMF특별인출권</code>, <code>현금 및 예금</code>, <code>채권</code>, <code>대출금</code>, <code>지분증권 및 투자펀드</code>, <code>파생금융상품</code>, <code>기타 수취채권 및 지급채무</code> 등이 이에 속한다.</li><li><code>금융상품</code>의 경우 한 경제주체가 보유하고 있는 <code>자산</code>은 다른 경제주체에게는 동일한 금액의 <code>부채</code>가 된다. <code>예금</code>은 가계의 <code>자산</code>인 동시에 은행으로서는 <code>부채</code>인 것이다.</li><li>반면 <code>비금융자산</code>은 금융자산과 달리 상응하는 부채가 없다. 예를 들어 가계가 보유하는 주택은 그 가계의 자산이지만 어느 누구의 부채도 아니다.</li><li>이와 같은 특성으로 금융상품은 국민대차대조표에서 자산과 부채가 동시에 기록되지만 비금융자산은 자산 항목으로만 기재된다.</li></ul></div><h2 id="국민대차대조표의-형태" tabindex="-1">국민대차대조표의 형태 <a class="header-anchor" href="#국민대차대조표의-형태" aria-label="Permalink to &quot;국민대차대조표의 형태&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">국민대차대조표의 형태</p><ul><li>국민대차대조표는 <code>기업</code>, <code>정부</code>, <code>가계 및 비영리단체</code>, <code>국외</code> 등 경제주체의 자산 및 부채를 <code>기초 대차대조표</code>, <code>증감표</code>, <code>기말 대차대조표</code> 등 3개의 표로 나누어 기록한다.</li><li><code>기초</code> 및 <code>기말 대차대조표</code>에는 국민경제 전체와 각 경제주체가 보유하고 있는 자산이 해당 시점의 시장가격으로 평가된 금액으로 기록되며 <code>증감표</code>에는 해당 기간 중 자산가치의 변동내역이 기록된다.</li><li><code>증감표</code>에 기록되는 <code>순자산(=자산-부채)</code> 규모의 변동은 <code>거래요인</code>과 <code>거래외요인</code>으로 구분될 수 있다.</li><li><code>거래요인</code>은 상호 거래(실물 및 금융 자산 취득)로 인해 경제주체의 순자산이 변동하는 것을 의미한다.</li><li><code>거래외요인</code>은 거래가 없이 순자산이 변동하는 것으로 자산가격의 변화, 예기치 못한 자연재해에 의한 자산파손, 지하자원의 발견 등이 이에 속한다.</li><li>한편 자산가격의 변화에 의한 순자산 증감분을 <code>명목보유손익</code>이라고 하는데 이는 기초와 기말의 두 시점 사이에 자산가격이 상승하거나 하락하면서 보유자산의 기말 평가금액이 달라져 발생하는 이익이나 손실을 말한다. <img src="'+t+'" alt="national-balance-sheet-form"></li></ul></div>',9)]))}const S=o(i,[["render",n]]);export{b as __pageData,S as default};
