import{_ as c,c as l,j as e,a as d,G as a,a2 as o,B as t,o as n}from"./chunks/framework.CdbxnhrM.js";const I=JSON.parse('{"title":"국민소득통계 - 국민소득 주요 지표","description":"GDP가 기본적으로 한 나라 내에서의 생산활동에 초점을 두고 있다면 한 나라 국민(거주자)의 소득은 국민소득 포괄범위에 따라 국민총소득(GNI), 국민처분가능소득(NDI) 등 다양한 지표로 측정되고 있다.","frontmatter":{"title":"국민소득통계 - 국민소득 주요 지표","description":"GDP가 기본적으로 한 나라 내에서의 생산활동에 초점을 두고 있다면 한 나라 국민(거주자)의 소득은 국민소득 포괄범위에 따라 국민총소득(GNI), 국민처분가능소득(NDI) 등 다양한 지표로 측정되고 있다.","head":[["meta",{"property":"og:title","content":"국민소득통계 - 국민소득 주요 지표"}],["meta",{"property":"og:description","content":"GDP가 기본적으로 한 나라 내에서의 생산활동에 초점을 두고 있다면 한 나라 국민(거주자)의 소득은 국민소득 포괄범위에 따라 국민총소득(GNI), 국민처분가능소득(NDI) 등 다양한 지표로 측정되고 있다."}],["meta",{"property":"og:image","content":"/images/haunblog.png"}],["meta",{"property":"og:url","content":"https://haun84.github.io/ko/economic-indicators/national-income-statistics/indicators"}]]},"headers":[],"relativePath":"ko/economic-indicators/national-income-statistics/indicators.md","filePath":"ko/economic-indicators/national-income-statistics/indicators.md","lastUpdated":null}'),p={name:"ko/economic-indicators/national-income-statistics/indicators.md"};function h(r,s,k,u,m,g){const i=t("AdsInarticle");return n(),l("div",null,[s[0]||(s[0]=e("h1",{id:"국민소득-주요-지표",tabindex:"-1"},[d("국민소득 주요 지표 "),e("a",{class:"header-anchor",href:"#국민소득-주요-지표","aria-label":'Permalink to "국민소득 주요 지표"'},"​")],-1)),s[1]||(s[1]=e("p",null,[e("strong",null,"GDP가 기본적으로 한 나라 내에서의 생산활동에 초점을 두고 있다면 한 나라 국민(거주자)의 소득은 국민소득 포괄범위에 따라 국민총소득(GNI), 국민처분가능소득(NDI) 등 다양한 지표로 측정되고 있다.")],-1)),a(i),s[2]||(s[2]=o(`<h2 id="국민소득지표" tabindex="-1">국민소득지표 <a class="header-anchor" href="#국민소득지표" aria-label="Permalink to &quot;국민소득지표&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">국민소득지표</p><ul><li><p>GDP가 기본적으로 한 나라 내에서의 생산활동에 초점을 두고 있다면 한 나라 국민(거주자)의 소득은 국민소득 포괄범위에 따라 <code>국민총소득(GNI: Gross National Income)</code>, <code>국민처분가능소득(NDI: National Disposal Income)</code> 등 다양한 지표로 측정되고 있다.</p></li><li><p>이들 국민소득지표는 특정한 유형의 소득을 포함허가나 배제한다는 점에서 GDP와 다르다. 여기서 한국은행이 작성, 발표하는 여러 가지 국민소득지표 가운데 가장 널리 사용되고 있는 <code>국민총소득</code>, <code>국민순소득</code>, <code>국민소득</code>, <code>국민처분가능소득</code>, <code>가계처분가능소득</code> 5개 지표를 중심으로 살펴보기로 한다.</p><ul><li>거주자의 여부는 노동, 자본 등 생산요소를 공급하는 주체이며 주된 경제적 이익의 중심이 어디에 있느냐를 기준으로 거주성을 판단하는데 일정 기간(통상 1년 이상) 어떤 나라에서 경제활동을 할 경우 그 나라의 거주자로 간주한다.</li></ul></li></ul></div><h2 id="국민총소득-gni" tabindex="-1">국민총소득(GNI) <a class="header-anchor" href="#국민총소득-gni" aria-label="Permalink to &quot;국민총소득(GNI)&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">국민총소득(GNI: Gross National Income)</p><ul><li><p>먼저 <code>국민총스독(GNI)</code>은 <code>한 나라의 국민이 일정 기간 동안 벌어들인 임금, 이자, 배당 등의 소득을 모두 합친 것</code>이다. 우리가 사는 세상에서는 국가 간에 재화와 서비스는 물론 노동이나 자본과 같은 생산요소도 활발하게 거래되고 있다. 즉, 우리나라 기업이 외국인을 고용하여 생산을 하기도 하고 반대로 우리 국민이 다른 나라에 나가 생산활동에 참여하기도 한다. 이처럼 생산요소의 국가 간 이동을 고려하여 <code>거주성</code> 개념, 즉 우리나라 국민을 기준으로 소득을 측정한 것이 GNI다.</p></li><li><p>따라서 <code>GNI</code>는 <code>GDP</code>에서 <code>우리나라 국민(거주자)들이 외국에서 벌어들인 소득을 더하고</code>, <code>외국인(비거주자)이 우리나라에서 벌어들인 소득을 뺀</code> 금액과 같아진다. 즉, GNI는 GDP에 국외수취요소소득을 더한 것에 국외지급요소소득을 제외한 아래의 식으로 구해진다.</p></li></ul><div class="language-md vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">md</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  명목 국민총소득(GNI) = 명목 국내총생산(GDP) + 명목 국외순수취요소소득* </span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">                     *(국외수취요소소득 - 국외지급요소소득)</span></span></code></pre></div><ul><li>앞에서 명목GNI는 명목GDP에 명목 국외순수취요소소득을 합한 것이라고 하였다. 그러면 실질구매력을 반영하는 실질GNI는 어떻게 산출될까? 명목GNI가 명목GDP로부터 계산할 수 있는 것처럼 <code>실질GNI</code>도 <code>실질GDP로부터 산출</code>된다. 그런데 명목GNI와 달리 실질GNI는 실질 국외순수취요소소득뿐만 아니라 <code>교역조건 변화에 따른 실질 무역손익까지 포함</code>하여 다름과 같이 계산된다.</li></ul><div class="language-md vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">md</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  실질 국민총소득(GNI) = 실질 국내총생산(GDP) + 교역조건 변화에 따른 실질 무역손익 </span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">                     + 실질 국외순수취요소소득</span></span></code></pre></div></div><div class="danger custom-block"><p class="custom-block-title">교역조건 변화에 따른 실질 무역손익</p><ul><li><p><code>교역조건</code>은 <code>수출가격을 수입가격으로 나눈 것</code>으로 <code>수출입 상품 간의 교환비율</code>이다. 교역조건이 변화하면 생산 및 소비가 영향을 받게 되고 그로 인해 국민소득이 변화하게 된다. 예를 들어 <code>교역조건이 나빠지면</code> 동일한 수출물량으로 사들일 수 있는 수입물량이 감소하게 된다. 이는 소비나 투자에 필요한 재화의 수입량이 줄어들어 <code>실질소득이 감소한 것을 의미</code>한다.</p></li><li><p>이처럼 교역조건이 변화하면 실질소득이 영향을 받기 때문에 실질GNI의 계산에는 <code>교역조건 변화에 따른 실질 무역손익</code>이 포함되는 것이다. <code>교역조건 변화에 따른 실질 무역손익</code>이란 교역조건의 변화로 인해 발생하는 실질소득의 국외 유출 또는 국외로부터의 유입을 말한다.</p></li><li><p><code>교역조건 변화에 따른 실질 무역손익</code>의 산출과정은 다음과 같다. 교역조건의 변화를 반영한 총 실질 무역손익[(X-M)/P]에서 기준연도로부터 교역조건이 변하지 않았다고 가정하였을 경우의 실질 무역손익 (x-m)을 차감한다.</p></li></ul><div class="language-md vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">md</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  T = (X - M) / P - (x - m)</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#E36209;--shiki-dark:#FFAB70;">  *</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> T : 교역조건 변화에 따른 실질 무역손익</span></span>
<span class="line"><span style="--shiki-light:#E36209;--shiki-dark:#FFAB70;">  *</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> X-M : 명목무역수지(당해년 가격 재화 및 서비스의 수출입차)</span></span>
<span class="line"><span style="--shiki-light:#E36209;--shiki-dark:#FFAB70;">  *</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> P : 환가지수(수출입 가격지수의 평균 = (Px + Pm)/2)</span></span>
<span class="line"><span style="--shiki-light:#E36209;--shiki-dark:#FFAB70;">  *</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> x-m : 교역조건 불변 실질 무역손익</span></span></code></pre></div></div>`,5)),a(i),s[3]||(s[3]=o(`<h2 id="국민순소득-nni" tabindex="-1">국민순소득(NNI) <a class="header-anchor" href="#국민순소득-nni" aria-label="Permalink to &quot;국민순소득(NNI)&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">국민순소득(NNI: Net National Income)</p><ul><li><p><code>국민순소득(NNI: Net National Income)</code>은 <code>국민총소득(GNI)</code>에서 <code>감가상각을 제외</code>한 것이다.</p></li><li><p><code>감가상각</code>은 한 경제가 보유하고 있는 공장, 기계설비, 구조물 등이 닳아 없어지는 것으로서 국민소득통계에서는 <code>고정자본소모</code>라고 한다. 일정 기간 중에 생산된 생산물의 순수한 가치는 그 기간 동안 생산능력이 저하되지 않고 그대로 유지된 상태에서 생산이 이루어지는 것으로 평가하여야 한다. 즉, 감가상각액을 생산물의 가치에서 제외하여야 한다는 의미이다.</p></li><li><p>자동차공장을 예로 들면 자동차를 만들기 위해서는 차체 등 각종 부품이 있어야 하며 또 각종 부품을 조립하는 기계도 있어야 한다. 그리고 자동차를 만들면 부품도 소비되지만 조립기계도 닳게 된다. 이 경우 자동차의 <code>산출액</code>에서 부품구입액 등 <code>중간투입액</code>을 <code>차감</code>한 것이 <code>총부가가치</code>이며, <code>총부가가치</code>에서 <code>감가상각액</code>을 뺀 것이 <code>순부가가치</code>이다.</p></li><li><p>기업 입장에서 감가상각은 현재와 같은 생산활동을 지속하기 위하여 언젠가 지출해야 하는 비용이다. 따라서 총생산보다는 본래의 기계가치를 그대로 유지하면서 새로이 생산해낸 순부가가치, 즉 순생산액에 더 큰 의미를 둘 것이다.</p></li></ul><div class="language-md vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">md</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  국민순소득(NNI) = 국민총소득(GNI) － 고정자본소모</span></span></code></pre></div></div><div class="danger custom-block"><p class="custom-block-title">GNI와 NNI</p><ul><li><p>그런데 실제로는 「<code>총액</code>」 개념인 GNI(또는 GDP)가 「<code>순액</code>」 개념인 NNI(또는 국내순생산(NDP: Net Domestic Product))보다 더 많이 사용되고 있는데 그 이유는 무엇일까?</p></li><li><p>OECD는 다음과 같이 설명하고 있다. 첫째로, 고정자본소모는 계산방법이 복잡하고 국가별로 서로 달라 고정자본소모의 규모에 대한 국가 간 비교가 어렵다는 것이다. 그리고 국민소득이나 성장률 등을 국가별로 비교하는 데 있어서 GNI(또는 GDP)와 NNI(또는 NDP)의 차이가 크지 않다는 점을 들고 있다.</p></li></ul></div><h2 id="국민소득-ni" tabindex="-1">국민소득(NI) <a class="header-anchor" href="#국민소득-ni" aria-label="Permalink to &quot;국민소득(NI)&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">국민소득(NI: National Income)</p><ul><li><p><code>국민소득(NI: National Income)</code>은 <code>한 나라의 거주자</code>들이 <code>재화와 서비스의 생산과정에서 벌어들인 소득을 합친 것</code>으로 정의된다. <code>NI</code>는 <code>NNI</code>에서 <code>간접세인 생산세 및 수입세를 빼고</code> <code>보조금을 더한 금액</code>이다.</p></li><li><p>한국은행이 발표하는 국민소득통계에서는 <code>NI</code>를 <code>요소비용 국민소득</code>이라고 정의하고 있다. <code>국민소득</code>이 <code>시장가격(market prices)</code>과 <code>요소비용(factor cost)</code>의 두 가지 가격에 의해 측정될 수 있기 때문이다.</p></li></ul><div class="language-md vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">md</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">국민소득(NI)(=요소비용 국민소득) = 국민순소득(NNI)(시장가격 국민소득) </span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">                            - 순생산세 및 수입세(생산세 및 수입세 －보조금)</span></span></code></pre></div></div><div class="danger custom-block"><p class="custom-block-title">요소비용</p><ul><li><p><code>요소비용</code>이란 <code>시장가격에서 순생산세 및 수입세를 공제한 것</code>으로서 결국 재화와 서비스의 생산에 사용된 생산요소에 대한 대가인 피용자보수와 영업잉여의 합계와 같아지는 것이다.</p></li><li><p>예를 들어 정부가 국민 건강에 이로운 우유의 생산을 장려하기 위해 우유 한 병에 50원의 보조금을 주는 한편 70원의 생산세가 붙어 병당 300원에 거래된다고 하자. 이때 우유의 원재료비가 1병당 100원이라면 우유 1병의 <code>시장가격에 의한 부가가치</code>는 200원(300원—100원)이다.</p></li><li><p>한편 <code>요소비용에 의한 부가가치</code>는 시장가격에 의한 부가가치 (200원)에 생산세(70원)를 빼고 보조금(50원)을 더한 180원이다. 즉, 요소비용에 의한 국민소득은 시장가격에 의한 국민소득보다 순생산세(20원)만큼 적다.</p></li></ul></div><div class="info custom-block"><p class="custom-block-title">본원소득과 처분가능소득</p><ul><li><p>이상에서 살펴본 국민소득은 생산활동에 참여한 대가로 벌어들인 소득, 즉 <code>본원소득(primary income)</code>을 대상으로 하고 있다.</p></li><li><p>실제로 국민이 소비하거나 저축할 수 있는 소득(<code>처분가능소득</code>)에는 <code>본원소득</code>뿐만 아니라 아무런 대가 없이 수취하는 <code>경상이전</code>도 있다. 경상이전은 한 나라 내의 경제주체 사이에서뿐만 아니라 우리나라 국민(거주자)과 외국인(비거주자) 간에도 발생한다.</p></li><li><p>이를 나타내는 국민소득지표에는 한 나라 전체의 거주자와 비거주자간에 발생하는 소득의 이전을 반영한 <code>국민처분가능소득(NDI)</code>과 가계와 정부 등 제도 부문 간 소득이전을 반영한 <code>가계처분가능소득(PDI: Personal Disposable Income)</code>이 있다.</p></li></ul></div>`,7)),a(i),s[4]||(s[4]=o(`<h2 id="국민처분가능소득-ndi" tabindex="-1">국민처분가능소득(NDI) <a class="header-anchor" href="#국민처분가능소득-ndi" aria-label="Permalink to &quot;국민처분가능소득(NDI)&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">국민처분가능소득(NDI)</p><ul><li><p><code>국민처분가능소득(NDI)</code>은 나라 경제 전체가 소비나 저축으로 <code>자유로이 처분할 수 있는 소득</code>으로서 거주자와 비거주자 간의 소득이전이 반영된 것이다.</p></li><li><p>즉, NDI는 NNI에 교포송금 등과 같이 생산활동과는 관계없이 외국으로부터 받은 소득(국외수취경상이전)을 더하고 무상원조 등 외국에 지급한 소득(국외지급경상이전)을 뺀 금액이다.</p></li><li><p>한편 NDI에 고정자본소모를 더한 것을 <code>국민총처분가능소득(GNDI: Gross National Disposable Income)</code>이라고 하는데 총저축률과 총투자율을 산출하는 데 이용된다.</p></li></ul><div class="language-md vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">md</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  국민처분가능소득(NDI) = 본원소득(=국민순소득(NNI)) + 국외순수취경상이전</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  </span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  국민총처분가능소득(GNDI) = 국민처분가능소득(NDI) + 고정자본소모</span></span></code></pre></div></div><h2 id="가계처분가능소득-pdi" tabindex="-1">가계처분가능소득(PDI) <a class="header-anchor" href="#가계처분가능소득-pdi" aria-label="Permalink to &quot;가계처분가능소득(PDI)&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">가계처분가능소득(PDI)</p><ul><li><code>가계처분가능소득(PDI)</code>은 가계와 다른 경제주체 간의 소득이전을 반영한 것으로 <code>가계가 임의로 소비나 저축으로 처분할 수 있는 소득</code>이다.</li><li>NI 가운데 가계에게 지급되지 않은 부분인 법인소득과 정부가 받은 이자, 임료 등을 차감하면 <code>가계본원소득(PPI: Personal Primary Income)</code>이 되는데 PDI는 PPI에 가계가 정부와 기업 및 국외로부터 <code>수취한 이전소득은 더하고</code>, 정부와 기업 및 국외에 <code>지급한 이전소득은 뺀</code> 금액이다.</li></ul><div class="language-md vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">md</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  가계처분가능소득(PDI) = 가계본원소득(PPI) + 순이전소득*</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">                      *소득·부 등에 대한 경상세, 사회부담금, 사회수혜금 등</span></span></code></pre></div></div><div class="danger custom-block"><p class="custom-block-title">가계조정처분가능소득(PADI: Personal Adjusted Disposable Income)</p><ul><li><p>한편 <code>가계처분가능소득</code>에 <code>사회적 현물이전(social transfers in kind)</code>까지 합한 것을 <code>가계조정처분가능소득(PADI: Personal Adjusted Disposable Income)</code> 이라고 한다.</p></li><li><p><code>사회적 현물이전</code>이란 정부 등이 가계에 현물 형태로 제공하는 재화와 서비스로서 무상교육, 보건소의 무상진료 등이 그 예이다. 사회적 현물이전은 저소득층 생계비 지원 등과 같이 정부가 가계에 현금으로 지원하는 정부의 경상이전지출과는 구별된다.</p></li></ul><div class="language-md vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">md</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  가계조정처분가능소득(PADI) = 가계처분가능소득(PDI) + 사회적 현물이전</span></span></code></pre></div></div>`,5))])}const b=c(p,[["render",h]]);export{I as __pageData,b as default};
