import{_ as t,c as a,a3 as o,o as l}from"./chunks/framework.BRvtVtzd.js";const y=JSON.parse('{"title":"거액결제시스템 (Large-Value Funds Payment System)","description":"","frontmatter":{"meta":[{"name":"title","content":"거액결제시스템 (Large-Value Funds Payment System)"},{"name":"description","content":"거액결제시스템은 고액의 자금 결제를 처리하기 위한 금융 인프라로, 주로 은행 간 대규모 자금 이동을 실시간으로 처리하는 시스템이다."},{"name":"keywords","content":"거액결제시스템 (Large-Value Funds Payment System), 실시간총액결제방식(RTGS; Real-Time Gross Settlement), 소액결제시스템"},{"property":"og:title","content":"거액결제시스템 (Large-Value Funds Payment System)"},{"property":"og:description","content":"거액결제시스템은 고액의 자금 결제를 처리하기 위한 금융 인프라로, 주로 은행 간 대규모 자금 이동을 실시간으로 처리하는 시스템이다."},{"property":"og:type","content":"website"},{"property":"og:image","content":"/images/haunblog.png"},{"property":"og:url","content":"https://haun84.github.io/ko/economic-words/large-value-funds-payment-system"}]},"headers":[],"relativePath":"ko/economic-words/large-value-funds-payment-system.md","filePath":"ko/economic-words/large-value-funds-payment-system.md","lastUpdated":null}'),s={name:"ko/economic-words/large-value-funds-payment-system.md"};function n(d,e,c,r,i,m){return l(),a("div",null,e[0]||(e[0]=[o('<h1 id="거액결제시스템-large-value-funds-payment-system" tabindex="-1">거액결제시스템 (Large-Value Funds Payment System) <a class="header-anchor" href="#거액결제시스템-large-value-funds-payment-system" aria-label="Permalink to &quot;거액결제시스템 (Large-Value Funds Payment System)&quot;">​</a></h1><h2 id="거액결제시스템-large-value-funds-payment-system-1" tabindex="-1"><code>거액결제시스템 (Large-Value Funds Payment System)</code> <a class="header-anchor" href="#거액결제시스템-large-value-funds-payment-system-1" aria-label="Permalink to &quot;`거액결제시스템 (Large-Value Funds Payment System)`&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">거액결제시스템 (Large-Value Funds Payment System)</p><ul><li><code>거액결제시스템</code>은 <code>고액의 자금 결제</code>를 처리하기 위한 <code>금융 인프라</code>로, 주로 <code>은행 간 대규모 자금 이동을 실시간으로 처리하는 시스템</code>이다.</li><li>이 시스템은 <code>금융 시스템의 안정성을 유지하기 위해 필수적</code>이며, <code>금융기관 간의 대규모 거래</code>, <code>중앙은행의 정책 집행</code>, 그리고 <code>금융시장 참가자 간의 결제를 지원</code>한다.</li><li><code>실시간총액결제방식(RTGS; Real-Time Gross Settlement)</code> 시스템이 대표적인 거액결제시스템의 예이다.</li><li>거액결제시스템은 금융 리스크를 줄이고, 금융 거래의 안전성과 효율성을 높이는 데 중요한 역할을 한다.</li></ul></div><div class="warning custom-block"><p class="custom-block-title">거액결제시스템 (Large-Value Funds Payment System)</p><ul><li><code>거액의 자금이체</code>가 참가기관 간에 자기계산으로 또는 고객을 대신하여 이루어지는 <code>자금이체시스템</code>을 말한다.</li><li><code>거액결제시스템(large-value funds payment system)</code>은 실제로 결제되는 지급지시에 최소금액이 설정된 것은 아니나 일반적으로 금융시장에서 이루어진 금융기관간 콜거래, 증권 또는 외환매매 등 거액거래에 따른 대금결제를 다루기 때문에 건당 결제금액이 매우 크다.</li><li>또한 한 나라의 금융기관간 거래자금 대부분을 결제하는 중요시스템으로서 결제시점 관리와 결제리스크 감축의 필요성이 높아 주요국의 거액결제시스템은 대부분 신용리스크를 제거할 수 있는 <code>실시간총액결제방식</code>을 채택하고 있으며 운영도 중앙은행이 직접 맡는 경우가 많다.</li><li><code>우리나라의 한은금융망(BOK-Wire+)</code>, <code>미국 연준의 Fedwire</code>, <code>유럽중앙은행(ECB)의 TARGET2</code> 등이 여기에 해당된다.</li></ul></div><h2 id="연관검색어" tabindex="-1">연관검색어 <a class="header-anchor" href="#연관검색어" aria-label="Permalink to &quot;연관검색어&quot;">​</a></h2><ul><li>소액결제시스템</li><li>지급결제시스템</li><li>총액결제시스템</li></ul>',6)]))}const p=t(s,[["render",n]]);export{y as __pageData,p as default};
