import{_ as r,c as o,a3 as c,o as t}from"./chunks/framework.BRvtVtzd.js";const m=JSON.parse('{"title":"가상통화 (Virtual Currency)","description":"가상통화는 물리적인 형태가 없는 디지털 화폐로, 암호화를 사용하여 거래를 보호하며, 대표적으로 비트코인, 이더리움 같은 암호화폐가 포함된다.","frontmatter":{"title":"가상통화 (Virtual Currency)","description":"가상통화는 물리적인 형태가 없는 디지털 화폐로, 암호화를 사용하여 거래를 보호하며, 대표적으로 비트코인, 이더리움 같은 암호화폐가 포함된다.","head":[["meta",{"name":"keywords","content":"가상통화 (Virtual Currency), 비트코인, 블록체인"}],["meta",{"property":"og:title","content":"가상통화 (Virtual Currency)"}],["meta",{"property":"og:description","content":"가상통화는 물리적인 형태가 없는 디지털 화폐로, 암호화를 사용하여 거래를 보호하며, 대표적으로 비트코인, 이더리움 같은 암호화폐가 포함된다."}],["meta",{"property":"og:image","content":"/images/haunblog.png"}],["meta",{"property":"og:url","content":"https://haun84.github.io/ko/economic-words/virtual-currency"}]]},"headers":[],"relativePath":"ko/economic-words/virtual-currency.md","filePath":"ko/economic-words/virtual-currency.md","lastUpdated":null}'),a={name:"ko/economic-words/virtual-currency.md"};function i(l,e,n,d,u,s){return t(),o("div",null,e[0]||(e[0]=[c('<h1 id="가상통화-virtual-currency" tabindex="-1">가상통화 (Virtual Currency) <a class="header-anchor" href="#가상통화-virtual-currency" aria-label="Permalink to &quot;가상통화 (Virtual Currency)&quot;">​</a></h1><h2 id="가상통화-virtual-currency-1" tabindex="-1"><code>가상통화 (Virtual Currency)</code> <a class="header-anchor" href="#가상통화-virtual-currency-1" aria-label="Permalink to &quot;`가상통화 (Virtual Currency)`&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">가상통화 (Virtual Currency)</p><ul><li><code>가상통화(virtual currency)</code>는 중앙은행이나 금융기관이 아닌 <code>민간</code>에서 <code>블록체인</code>을 기반기술로 하여 발행･유통되는 <code>가치의 전자적 표시</code>(digital representation of value)로서 <code>비트코인</code>이 가장 대표적인 가상통화이다.</li><li><code>비트코인</code> 등장 이전에는 특별한 법적 근거 없이 민간기업이 발행하고 인터넷공간에서 사용되는 <code>사이버머니(게임머니 등)</code>나 온･오프라인에서 사용되고 있는 각종 <code>포인트</code>를 가상통화로 통칭하였다. 그러나 2009년 비트코인이 등장하면서 가상통화의 개념이 변화되고 있다.</li><li><code>비트코인</code>은 <code>블록체인기술</code>을 기반으로 하여 중앙운영 기관 없이 <code>P2P(peer-to-peer) 거래</code>가 가능한 <code>분산형 시스템</code>을 통해 발행･유통된다는 점에서 발행기관이 중앙에서 발행･유통을 통제하는 기존의 사이버머니나 멤버십 포인트 등과 기반이 완전히 상이하기 때문이다.</li><li>현재 비트코인 거래가 크게 늘어나고 가격도 급등한 가운데 비트코인 이외에 많은 <code>신종코인(Alt-coin)</code>도 출현하면서 이들 가상통화를 구분할 필요성이 발생하였다.</li><li>이에 따라 최근 IMF 등 국제기구에서는 비트코인류의 가상통화를 <code>암호통화(cryptocurrency)</code>로 부르면서 종래의 가상통화의 하위 개념으로 분류하고 있는 추세이다.</li></ul></div><h2 id="연관검색어" tabindex="-1">연관검색어 <a class="header-anchor" href="#연관검색어" aria-label="Permalink to &quot;연관검색어&quot;">​</a></h2><ul><li>블록체인</li><li>비트코인</li></ul>',5)]))}const y=r(a,[["render",i]]);export{m as __pageData,y as default};
