import{_ as a,c as o,a3 as l,o as t}from"./chunks/framework.BRvtVtzd.js";const h=JSON.parse('{"title":"임신 사전건강관리 지원 금액과 신청 방법","description":"4월 1일부터 소득수준과 거주지역에 상관없이 임신을 준비 중인 부부라면 가임력 검사비를 지원받을 수 있어요. 지원 대상에는 사실혼, 예비부부도 포함돼요.","frontmatter":{"title":"임신 사전건강관리 지원 금액과 신청 방법","description":"4월 1일부터 소득수준과 거주지역에 상관없이 임신을 준비 중인 부부라면 가임력 검사비를 지원받을 수 있어요. 지원 대상에는 사실혼, 예비부부도 포함돼요.","head":[["meta",{"name":"keywords","content":"돈되는 정책, 문화체육관광부, 정책 주간지 K-공감, 임신 사전건강관리 지원, 가임력 검사비 지원"}],["meta",{"property":"og:title","content":"임신 사전건강관리 지원 금액과 신청 방법"}],["meta",{"property":"og:description","content":"4월 1일부터 소득수준과 거주지역에 상관없이 임신을 준비 중인 부부라면 가임력 검사비를 지원받을 수 있어요. 지원 대상에는 사실혼, 예비부부도 포함돼요."}],["meta",{"property":"og:image","content":"/images/haunblog.png"}],["meta",{"property":"og:url","content":"https://haun84.github.io/ko/feed/money-policy/pre-pregnancy-support"}]]},"headers":[],"relativePath":"ko/feed/money-policy/pre-pregnancy-support.md","filePath":"ko/feed/money-policy/pre-pregnancy-support.md","lastUpdated":null}'),r={name:"ko/feed/money-policy/pre-pregnancy-support.md"};function i(c,e,n,d,p,s){return t(),o("div",null,e[0]||(e[0]=[l('<h1 id="임신-사전건강관리-지원-금액과-신청-방법" tabindex="-1">임신 사전건강관리 지원 금액과 신청 방법 <a class="header-anchor" href="#임신-사전건강관리-지원-금액과-신청-방법" aria-label="Permalink to &quot;임신 사전건강관리 지원 금액과 신청 방법&quot;">​</a></h1><h2 id="임신-사전건강관리-지원-내용" tabindex="-1">임신 사전건강관리 지원 내용 <a class="header-anchor" href="#임신-사전건강관리-지원-내용" aria-label="Permalink to &quot;임신 사전건강관리 지원 내용&quot;">​</a></h2><p>4월 1일부터 <code>소득수준과 거주지역에 상관없이 임신을 준비 중인 부부라면 가임력 검사비를 지원받을 수 있어요.</code> 지원 대상에는 사실혼, 예비부부도 포함돼요.</p><p><code>지원 비용은 최대 18만 원이에요.</code> 여성 난소기능 검사비 및 초음파 검사비(13만 원)와 남성 정액 검사비 (5만원)를 지원 받을 수 있어요.</p><p>검사 신청은 주소지 관할 보건소나 e보건소를 통해 할 수 있어요. 검사 신청을 하면 ‘검사의뢰서&#39;를 발급받을 수 있는데요.</p><p>3개월 내에 검사의뢰서를 가지고 사업에 참여한 전국 1,000여 개의 의료기관에서 검사받으면 돼요. 의료기관에 비용을 먼저 지불하면, 추후 보건소를 통해 돌려받을 수 있어요.</p><blockquote><p>🔗 <a href="https://www.e-health.go.kr/gh/caSrvcGud/selectMdclSupGudInfo.do?heBiz=PG00003&amp;menuId=200097" target="_blank" rel="noreferrer">검사 가능한 의료기관 리스트(임신 사전건강관리 참여 의료기관 현황, 24.04.01 기준)</a></p></blockquote><h2 id="지원-대상" tabindex="-1">지원 대상 <a class="header-anchor" href="#지원-대상" aria-label="Permalink to &quot;지원 대상&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">지원 대상</p><ul><li>임신을 희망하는 부부(사실혼, 예비부부 포함) <ul><li>1인 1회 지원</li><li>부부 중 여성이 가임기(15~49세, WHO 기준)인 경우에 한하여 지원</li><li>자체 유사 사업(서울시 남녀 임신준비 지원사업) 시행 중인 서울시는 제외</li></ul></li></ul></div><h2 id="지원-금액" tabindex="-1">지원 금액 <a class="header-anchor" href="#지원-금액" aria-label="Permalink to &quot;지원 금액&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">지원 금액</p><ul><li><code>여성</code>: 최대 13만 원</li><li><code>남성</code>: 최대 5만 원</li></ul></div><h2 id="지원-항목" tabindex="-1">지원 항목 <a class="header-anchor" href="#지원-항목" aria-label="Permalink to &quot;지원 항목&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">지원 항목</p><ol><li>필수 검사 항목 <ul><li>여성: 난소기능검사, 부인과(난소, 자궁 등) 초음파</li><li>남성: 정액검사(정자정밀형태검사 포함)</li></ul></li><li>지원 금액 한도 내 진찰료 및 기타 검사비 지원 가능</li></ol></div><h2 id="신청-기간" tabindex="-1">신청 기간 <a class="header-anchor" href="#신청-기간" aria-label="Permalink to &quot;신청 기간&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">신청 기간</p><ul><li>2024년 4월 1일 이후 연중 신청</li></ul></div><h2 id="지원-신청-절차-및-방법" tabindex="-1">지원 신청 절차 및 방법 <a class="header-anchor" href="#지원-신청-절차-및-방법" aria-label="Permalink to &quot;지원 신청 절차 및 방법&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">지원 신청 절차 및 방법</p><ol><li><code>검사비 지원 신청</code>: 보건소 방문 또는 <a href="https://e-health.go.kr" target="_blank" rel="noreferrer">e보건소</a> 온라인 신청 가능</li><li><code>검사의뢰서 발급</code>: 대상자 여부 확인 후 검사 의뢰서 발급</li><li><code>검사 실시 및 결과 상담</code></li><li><code>검사비 청구</code>: 보건소 또는 <a href="https://e-health.go.kr" target="_blank" rel="noreferrer">e보건소</a> 통해 청구서 등 필수서류* 제출</li><li><code>검사비 지급</code></li></ol></div><ul><li>자세한 신청 방법과 필수 서류는 <a href="https://www.e-health.go.kr/gh/caSrvcGud/selectMdclSupGudInfo.do?heBiz=PG00003&amp;menuId=200097" target="_blank" rel="noreferrer">여기서</a> 확인해 주세요.</li></ul><blockquote><p>이 글은 2024년 4월 19일 기준으로 작성되었습니다.</p></blockquote>',19)]))}const m=a(r,[["render",i]]);export{h as __pageData,m as default};
