import{_ as o,c as a,a3 as t,o as i}from"./chunks/framework.BRvtVtzd.js";const m=JSON.parse('{"title":"병원 가기 전 비급여 진료비 미리 확인하는 법","description":"병원에 가기 전 인터넷과 모바일을 통해 비급여 진료비를 미리 검색할 수 있어요","frontmatter":{"title":"병원 가기 전 비급여 진료비 미리 확인하는 법","description":"병원에 가기 전 인터넷과 모바일을 통해 비급여 진료비를 미리 검색할 수 있어요","head":[["meta",{"property":"og:title","content":"병원 가기 전 비급여 진료비 미리 확인하는 법"}],["meta",{"property":"og:description","content":"병원에 가기 전 인터넷과 모바일을 통해 비급여 진료비를 미리 검색할 수 있어요"}],["meta",{"property":"og:image","content":"/images/haunblog.png"}],["meta",{"property":"og:url","content":"https://haun84.github.io/ko/feed/money-policy/uninsured-medical-expenses"}]]},"headers":[],"relativePath":"ko/feed/money-policy/uninsured-medical-expenses.md","filePath":"ko/feed/money-policy/uninsured-medical-expenses.md","lastUpdated":null}'),l={name:"ko/feed/money-policy/uninsured-medical-expenses.md"};function c(d,e,n,s,r,p){return i(),a("div",null,e[0]||(e[0]=[t('<h1 id="병원-가기-전-비급여-진료비-미리-확인하는-법" tabindex="-1">병원 가기 전 비급여 진료비 미리 확인하는 법 <a class="header-anchor" href="#병원-가기-전-비급여-진료비-미리-확인하는-법" aria-label="Permalink to &quot;병원 가기 전 비급여 진료비 미리 확인하는 법&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">Language</p><ul><li><a href="/feed/money-policy/uninsured-medical-expenses">English Page: 영어 페이지</a></li></ul></div><h2 id="비급여-진료비" tabindex="-1">비급여 진료비 <a class="header-anchor" href="#비급여-진료비" aria-label="Permalink to &quot;비급여 진료비&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">비급여 진료비</p><ul><li>병원에 가기 전 <code>인터넷</code>과 <code>모바일</code>을 통해 <code>비급여 진료비</code>를 미리 검색할 수 있어요.</li><li><code>의료보험이 적용되지 않는 항목</code>을 <code>비급여 항목</code>이라고 하는데요. 병원마다 비급여 진료비용 간 차이가 커서 환자들은 혼란과 부담을 느끼곤 했어요.</li><li>9월 5일부터 <a href="https://www.hira.or.kr" target="_blank" rel="noreferrer">심사평가원 홈페이지</a>과 모바일 애플리케이션(앱) <code>건강e음</code>을 통해 병원별 비급여 진료비용을 검색할 수 있어요.</li><li>올해는 전체 의료기관을 대상으로 <code>자기공명영상(MRI)</code>, <code>초음파검사료</code>, <code>예방접종</code> 등 623개 비급여 항목의 가격을 먼저 공개해요.</li><li>정부가 2024년 비급여 진료비용을 조사한 결과 전체 항목의 65.7%에서 평균 가격이 오른 것으로 나타났어요. 국민의 관심이 높은 도수치료는 평균 가격이 전년 대비 2.5%, 대상포진 예방접종비는 10.6% 인상됐어요. 특히 도수치료와 백내장 수술용 다초점렌즈 등은 의료기관별 가격 차이가 무척 컸는데요. 서울 A의원은 도수치료비가 10만 원인 반면 경남 B의원은 26만 원에 이르렀어요. 다초점렌즈의 경우 서울 C의원은 약 29만 원, D의원은 680만 원으로 가격 차가 최대 20배 넘게 차이가 났어요.</li></ul></div><h2 id="검색방법" tabindex="-1">검색방법 <a class="header-anchor" href="#검색방법" aria-label="Permalink to &quot;검색방법&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">검색방법</p><ul><li>심사평가원 홈페이지 <code>조회·신청→비급여 진료비 정보</code> 메뉴 클릭</li><li><code>비급여 진료비용 상세 검색</code> 화면에서 지역, 의료기관 종류, 비급여 진료비 항목 입력 후 확인</li><li>지역 내 동일 규모 의료기관의 ‘중간금액’까지 화면에 표시되기 때문에 일일이 모든 병원을 클릭하지 않아도 돼요</li></ul></div>',6)]))}const h=o(l,[["render",c]]);export{m as __pageData,h as default};
