import{_ as o,c as r,a2 as l,G as i,j as e,a as s,B as n,o as c}from"./chunks/framework.CdbxnhrM.js";const g=JSON.parse('{"title":"콜시장 거래현황","description":"콜거래는 글로벌 금융위기와 콜시장 제도 개편 과정을 거치면서 위축되어 기간중 일평균 잔액이 2010년 전후 30조원 내외 수준에서 2021년 상반기중 9.7조원으로 크게 감소하였다.","frontmatter":{"description":"콜거래는 글로벌 금융위기와 콜시장 제도 개편 과정을 거치면서 위축되어 기간중 일평균 잔액이 2010년 전후 30조원 내외 수준에서 2021년 상반기중 9.7조원으로 크게 감소하였다.","head":[["meta",{"property":"og:title","content":"콜시장 거래현황"}],["meta",{"property":"og:description","content":"콜거래는 글로벌 금융위기와 콜시장 제도 개편 과정을 거치면서 위축되어 기간중 일평균 잔액이 2010년 전후 30조원 내외 수준에서 2021년 상반기중 9.7조원으로 크게 감소하였다."}],["meta",{"property":"og:url","content":"https://haun84.github.io/ko/financial-markets-in-korea/money-markets/call-market/market-trends"}]]},"headers":[],"relativePath":"ko/financial-markets-in-korea/money-markets/call-market/market-trends.md","filePath":"ko/financial-markets-in-korea/money-markets/call-market/market-trends.md","lastUpdated":null}'),d={name:"ko/financial-markets-in-korea/money-markets/call-market/market-trends.md"};function m(k,a,u,p,h,b){const t=n("AdsInarticle");return c(),r("div",null,[a[0]||(a[0]=l('<h1 id="콜시장-거래현황" tabindex="-1">콜시장 거래현황 <a class="header-anchor" href="#콜시장-거래현황" aria-label="Permalink to &quot;콜시장 거래현황&quot;">​</a></h1><p><strong>콜거래는 글로벌 금융위기와 콜시장 제도 개편 과정을 거치면서 위축되어 기간중 일평균 잔액이 2010년 전후 30조원 내외 수준에서 2021년 상반기중 9.7조원으로 크게 감소하였다.</strong></p><div class="info custom-block"><p class="custom-block-title">Language</p><ul><li><a href="/financial-markets-in-korea/money-markets/call-market/market-trends">English Page: 영어 페이지</a></li></ul></div>',3)),i(t),a[1]||(a[1]=l('<h2 id="콜시장-거래규모-추이" tabindex="-1">콜시장 거래규모 추이 <a class="header-anchor" href="#콜시장-거래규모-추이" aria-label="Permalink to &quot;콜시장 거래규모 추이&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">거래규모 추이</p><ul><li>콜거래는 글로벌 금융위기와 콜시장 제도 개편 과정을 거치면서 위축되어 기간중 일평균 잔액이 2010년 전후 30조원 내외 수준에서 2021년 상반기중 9.7조원으로 크게 감소하였다.</li><li>2008〜2009년중에는 글로벌 금융위기의 여파로 금융시장내 디레버리징(deleveraging)이 확산되면서 외은지점의 콜차입이 줄어든 가운데 단기금융시장 경색으로 증권회사들도 일시적으로 콜차입에 어려움을 겪으면서 콜거래가 크게 감소하였다. 2011~2015년중에는 증권회사 콜차입 한도 규제 등으로 비은행권의 콜차입이 감소하였으며, 2015년 이후에는 자산운용회사 콜론 한도 규제, 은행 유동성커버리지비율(이하 ‘LCR’; Liquidity Coverage Ratio) 규제 도입 등으로 인해 은행권의 콜차입도 축소되었다.</li></ul></div><h3 id="은행의-콜거래-축소-배경" tabindex="-1">은행의 콜거래 축소 배경 <a class="header-anchor" href="#은행의-콜거래-축소-배경" aria-label="Permalink to &quot;은행의 콜거래 축소 배경&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">은행의 콜거래 축소 배경</p><ul><li>은행의 콜거래가 은행 중심의 콜시장 개편이 완료된 2015년 이후에도 꾸준히 줄어들면서 전체 콜시장 규모도 지속적으로 감소하는 추세이다. 시장참가자별로 보면 국내은행의 콜머니가 상당폭 감소하였으며, 2016년 이후에는 콜론도 꾸준히 감소하고 있다. 외은지점도 2017년 이후 콜거래 감소폭이 확대되고 있다.</li><li>특히 2021년 상반기에는 국내은행과 외은지점의 콜론·콜머니 모두 큰 폭 감소하였다. 은행의 콜거래 축소를 주도한 국내은행의 콜차입 감소는 우선 콜시장 규제 강화에 기인한다. 자산운용회사 콜론 한도 도입으로 국내은행들은 비은행금융기관으로부터의 콜차입을 제약받게 되었다. 또한 2019년까지 단계적으로 강화되어온 LCR 규제도 국내은행의 콜머니를 축소하는 요인으로 작용하였다.</li><li>은행의 단기자금조달이 많아질 경우 향후 30일간의 순현금유출 규모가 커져 LCR이 하락하게 되므로, 국내은행은 정기예금, 금융채, CD 등을 통한 자금조달을 늘리고 콜머니 등 단기자금조달 규모를 축소하는 방향으로 LCR 규제에 대응하였다. 여기에 더해 2021년중에는 코로나19 위기 상황에 대응하여 이례적인 수준으로 완화된 금융여건 등으로 지준시장 잉여세가 지속됨에 따라 국내은행의 콜머니 수요가 감소하였다.</li><li>한편 2016년 이후 국내은행의 콜론이 지속적으로 감소한 것은 일시 여유자금을 금리 메리트가 높아진 RP로 운용하는 규모를 늘린 데 주로 기인한다. 국내은행이 콜·RP시장에 공급하는 자금 중에서 RP로의 운용비중은 2016년 43%에서 2021년 상반기 83%로 크게 상승하였다. 이러한 국내은행의 콜거래 축소는 외은지점의 콜거래 위축으로 이어졌다. 외은지점은 국내은행의 콜차입 수요 감소 등으로 콜자금 운용여건이 악화되자 콜론을 축소하고 증권사 대상 RP매수를 확대하였다. 또한 국내은행이 콜론 규모를 지속적으로 줄이는 가운데 상대적으로 금리가 높은 증권사에 대한 콜론비중을 대폭 확대(2016년 19.8%→2021년 상반기 30.5%)하는 한편 외은지점이 본점 차입을 늘리면서 외은지점 콜머니 규모도 감소하였다.</li></ul></div>',4)),i(t),a[2]||(a[2]=l('<h2 id="참가기관별-거래-현황" tabindex="-1">참가기관별 거래 현황 <a class="header-anchor" href="#참가기관별-거래-현황" aria-label="Permalink to &quot;참가기관별 거래 현황&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">참가기관별 거래 현황</p><ul><li>콜시장 참가기관별 거래 비중을 보면 콜론의 경우 2021년 상반기중 자산운용사가 42.8%로 가장 높으며 국내은행과 외은지점이 각각 37.3%와 18.9%를 차지하고 있다. 국내은행의 비중은 2015년 3월 자산운용회사 콜론 한도 규제 도입 이후 2020년까지 가장 높은 수준을 유지하였으나 2021년 상반기에는 이례적으로 완화된 금융여건 등으로 자산운용사 비중을 하회하였다. 콜머니의 경우에는 증권회사 비중이 37.0%로 가장 높고 그 다음은 국내은행(36.8%), 외은지점(21.3%) 순이다.</li></ul></div><h2 id="거래방식별-거래-현황" tabindex="-1">거래방식별 거래 현황 <a class="header-anchor" href="#거래방식별-거래-현황" aria-label="Permalink to &quot;거래방식별 거래 현황&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">거래방식별 거래 현황</p><ul><li>거래방식별로 보면 2015년 이후 담보 콜거래는 전무하며 무담보 콜거래만 이루어지고 있다. 무담보 콜거래의 경우 중개거래가 대부분이며 직거래는 미미한 수준을 보이고 있다. 2021년 상반기 중 전체 콜거래(일평균 8.5조원) 가운데 직거래는 0.01%인 4억원에 불과하다.</li></ul></div>',4)),i(t),a[3]||(a[3]=e("h2",{id:"콜금리-동향",tabindex:"-1"},[s("콜금리 동향 "),e("a",{class:"header-anchor",href:"#콜금리-동향","aria-label":'Permalink to "콜금리 동향"'},"​")],-1)),a[4]||(a[4]=e("div",{class:"tip custom-block"},[e("p",{class:"custom-block-title"},"콜금리 동향"),e("ul",null,[e("li",null,"콜금리는 한국은행이 매일 3개 자금중개회사를 통한 중개거래와 금융기관간 직거래를 포함한 전체 무담보 익일물 거래에 대해 거래금액 가중평균 방식으로 산출하여 공표하고 있다."),e("li",null,"기간별로 보면 2008~2009년 중에는 글로벌 금융위기 대응과정에서의 유동성 공급으로 콜금리가 기준금리를 비교적 큰 폭 하회하였다가 2010년 이후 기준금리 수준에서 소폭 등락하면서 안정되었다. 2014년 이후에는 대체로 기준금리를 소폭 하회하는 모습을 지속하다가 2018년 이후에는 다시 기준금리를 중심으로 소폭 등락하고 있다.")])],-1))])}const v=o(d,[["render",m]]);export{g as __pageData,v as default};
